import React, {Component} from 'react';
import config from "../../config.json";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";


class MealInfo extends Component{
    constructor(props){
        super(props)
        this.state = {
            meal: {}
        }
    }

    componentDidMount(){
        const id = this.props.match.params.id
        const storageCheck = JSON.parse(localStorage.getItem(id));
        const options = {
            method: 'GET',
            url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + id + '/information',
            headers: {
            'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
            'X-RapidAPI-Key': config["api-key"]
            }
        };
        if(storageCheck && (Date.now() < storageCheck.ttl)){
            this.setState({meal: storageCheck})
            console.log(storageCheck)
        }
        else{
            axios.request(options).then(res => {
                res.data.ttl = Date.now() + (86400 * 1000);
                localStorage.setItem(id, JSON.stringify(res.data));
                this.setState({meal: res.data})
                
            }).catch(err => {
                console.error(err)
            })
        }
    }

    render(){
        const id = this.props.match.params.id
        let meal = this.state.meal
        return(
            <>
            <div class="clearfix">
                <img src={"https://spoonacular.com/recipeImages/" + id + "-240x150.jpg"} class="col-md-6 float mb-3 ms-md-3"></img>
            </div>
            <h1>{meal.title}</h1>
            </>
        )
    }
}

export default MealInfo;
