import React, { Component } from 'react';
import axios from 'axios'
import "bootstrap/dist/css/bootstrap.min.css";
import "./Mealplan.css"
import { Link, Router } from 'react-router-dom';

class Mealplans extends Component {
    constructor(props){
        super(props)
        
        this.state = {
            mealplanNames: []
        }
    }

    componentDidMount(){
        axios.get('http://localhost:5000/mealplan')
        .then(res => {
            const mealplans = Array.from(res.data);
            console.log(mealplans);
            this.setState({
                mealplanNames: mealplans.map((plan) => 
                <li><Link to="" class="list-group-item">
                    {plan.name}
                    <span class="badge bg-primary rounded-pill">2</span>
                </Link></li>)
            });
        })
        // console.log(this.state.mealplanNames)
    }
        
    render() {
        return (
            <div class="container" id="mealplan-container">
                <h2>Meal Plans</h2>
                <hr />
                <ul class="list-group list-group-flush">
                    {this.state.mealplanNames}
                </ul>
            </div>
           
            
            
        )
    }
}

export default Mealplans
