import React, { Component } from 'react';
import axios from 'axios'
import "bootstrap/dist/css/bootstrap.min.css";
import "./Mealplan.css"
import { Link, Router } from 'react-router-dom';

class Mealplans extends Component {
    constructor(props){
        super(props)
        
        this.state = {
            mealplanNames: []
        }
        this.onClickHandler = this.onClickHandle.bind(this)
    }
    onClickHandle = (event) =>{
         alert();
         event.stopPropagation();

    }

    linkHandler = (event, isEventTarget) =>{
        event.preventDefault();
        if(isEventTarget){
            alert();
        }
    }

   


    componentDidMount(){
        axios.get('http://localhost:5000/mealplan')
        .then(res => {
            const mealplans = Array.from(res.data);
            console.log(mealplans);
            this.setState({
                mealplanNames: mealplans.map((plan) => 
                <a href="/" class="list-group-item list-group-item-action d-flex justify-content-between align-items-center border-end-0 border-start-0" 
                onClick={(event) => this.linkHandler(event, (event.target).is("<a>"))}>
                    {plan.name}
                    <span class="delete-button" onClick={event => this.onClickHandle(event)}></span>
                </a>)
            });
        })
        // console.log(this.state.mealplanNames)
    }
        
    render() {
        return (
            <div class="container">
                <h2>Meal Plans</h2>
                {/* <hr /> */}
                <div class="list-group list-group-flush">
                    {this.state.mealplanNames}
                </div>
            </div>
           
            
            
        )
    }
}

export default Mealplans
