import React, { Component } from 'react';
import axios from 'axios'
import "bootstrap/dist/css/bootstrap.min.css";
import { Link, Router } from 'react-router-dom';

class Mealplans extends Component {
    constructor(props){
        super(props)
        
        this.state = {
            mealplanNames: []
        }
    }

    componentDidMount(){
        axios.get('http://localhost:5000/mealplan')
        .then(res => {
            const mealplans = res.data;
            this.setState({
                mealplanNames: mealplans.map((plan) => <li><Link to="" class="list-group-item list-group-item-action">{plan.monday.name}</Link></li>)
            });
        })
        console.log(this.state.mealplanNames)
    }
        
    render() {
        return (
            <div class="list-group">
                {/* {this.state.mealplanListGroup} */}
            </div>
            
            
        )
    }
}

export default Mealplans
