import React, {Component} from 'react';
import config from "../../config.json";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";


class MealInfo extends Component{
    constructor(props){
        super(props)
        this.state = {
            meal: {},
            images: <></>
        }
    }

    componentDidMount(){
        const id = this.props.match.params.id
        const storageCheck = JSON.parse(localStorage.getItem(id));
        const options = {
            method: 'GET',
            url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + id + '/information',
            headers: {
            'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
            'X-RapidAPI-Key': config["api-key"]
            }
        };
        if(storageCheck && (Date.now() < storageCheck.ttl)){
            this.setState({meal: storageCheck})
            const imageList = storageCheck.extendedIngredients.map((ingredient) => <li><img
            style={{width: "50px", height: "50x"}} src={"https://spoonacular.com/cdn/ingredients_100x100/" + ingredient.image}></img><h1>{ingredient.originalName}</h1></li>)
                this.setState({meal: storageCheck, 
                    images: imageList})         
            console.log(storageCheck)
        }
        else{
            axios.request(options).then(res => {
                res.data.ttl = Date.now() + (86400 * 1000);
                localStorage.setItem(id, JSON.stringify(res.data));
                const imageList = res.data.extendedIngredients.map((ingredient) => <li><img
                style={{width: "50px", height: "50x"}} src={"https://spoonacular.com/cdn/ingredients_100x100/" + ingredient.image}></img><h1>{ingredient.originalName}</h1></li>)
                this.setState({meal: res.data, 
                    images: imageList})                
            }).catch(err => {
                console.error(err)
            })
        }
    }

    render(){
        const id = this.props.match.params.id
        let meal = this.state.meal
        console.log(meal)
        return(
            <>
            <div class="clearfix" style={{backgroundColor: 'white'}}>
                <img style={{width: "556px", height: "370px"}} src={"https://spoonacular.com/recipeImages/" + id + "-556x370.jpg"} align="left" class="col-md-8 float mb-3 mt-2 me-5 ms-md-3"></img>
                <h1>{meal.title}</h1>
                <p dangerouslySetInnerHTML={{__html: meal.summary}}></p>
            </div>
            <div style={{backgroundColor: 'white'}}>
                <ul>
                    {this.state.images}
                </ul>
            </div>
            
            </>
        )
    }
}

export default MealInfo;
