import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { Modal } from 'react-bootstrap';
import config from "../../config.json";



class EditModal extends Component{
    constructor(props){
        super(props);
        this.state = {
            nameField: "",
        }
    }
    changeName = (event) => {
        event.preventDefault();
        axios.post('http://localhost:5000/mealplan/editName/' + this.props.match.params.id + '/' + this.state.nameField);
    }

    render(){
        return(
            <Modal show={this.props.show} size="lg">
                <Modal.Header>Rename Mealplan</Modal.Header>
                <Modal.Body>
                    <form id="rename-form" style={{left: '0vw', width: '100%'}}>
                        
                        <div class="form-outline mb-4 input-group inner-addon right-addon">
                            
                            <input type="text" id="rename" style={{left: '0vw', width: '100%'}} placeholder='Enter a New Name!' 
                            value={this.state.nameField} onChange={event => {this.setState({nameField: event.target.value})}}/>
                            
                        </div>
                        <button type="submit" style={{position: "absolute", width: "100%", top: "10vh"}} onClick={(event) => {this.changeName(event); this.props.closeFunc()}}>Save</button>
                    </form>
                </Modal.Body>
            </Modal>
        )
    }
}

export default EditModal;
