import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import emptysign from "../MealPlan/emptyplate.webp"
import MealsModal from "./MealsModal";
import config from "../../config.json";

class Meal extends Component{
    constructor(props){
        super(props)
        this.state = {
            showModal: false,
            meal: {title: ""},
            title: ""
        }
        
    }

    // componentDidMount(){
    getData = async (options, storageCheck) => {
        if(storageCheck && (Date.now() < storageCheck.ttl)){
            return storageCheck
        }
        else{
            try{
                let res = await axios.request(options)
                res.data.ttl = Date.now() + (86400 * 1000);
                localStorage.setItem(this.props.id, JSON.stringify(res.data));
                return res.data
            }
            catch(err){
                console.error(err)
            }
        }
        // axios.request(options).then(res => {
        //     console.log()
        //     res.data.ttl = Date.now() + (86400 * 1000);
        //     localStorage.setItem(this.props.id, JSON.stringify(res.data));
            
        // }).catch(function (error) {
        //     console.error(error);
        // });
    }

    componentDidMount(){
        if(this.props.id !== ""){
            const storageCheck = JSON.parse(localStorage.getItem(this.props.id));
            const options = {
                method: 'GET',
                url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + this.props.id + '/information',
                headers: {
                'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
                'X-RapidAPI-Key': config["api-key"]
                }
            };
        
            
            console.log("we reached here")
            const data = this.getData(options, storageCheck)
            this.setState({meal: data})
            console.log(this.state)
        }
        
    }
    
    showModalClick = () => {
        alert();
        this.setState({
            showModal: true
        })
    }
    render(){
        let imageUrl = emptysign
        let viewMeal = <a href="#" style={{color: "rgb(41, 198, 254)"}}> View Meal</a>
        let meal = {title: ""}
        let changeMealFunc = (newMeal) => {}
        if(this.props.id !== ""){
            const storageCheck = JSON.parse(localStorage.getItem(this.props.id));
            const options = {
                method: 'GET',
                url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + this.props.id + '/information',
                headers: {
                  'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
                  'X-RapidAPI-Key': config["api-key"]
                }
            };
           
            
            console.log("we reached here")
            imageUrl = "https://spoonacular.com/recipeImages/" + this.props.id + "-240x150.jpg"
            viewMeal = <a href="#" style={{color: "rgb(41, 198, 254)"}}> View Meal</a>
        
        }
        let search = <></>
        if(this.props.edit){
            search = <button class="btn btn-primary" onClick={this.showModalClick}></button>
            changeMealFunc = this.props.changeMealFunc
        }
        return(
            <div class="card" style={{width: "170px", backgroundColor: "rgb(48, 51, 54)", color: "whitesmoke", marginBottom: "2rem"}} onClick={this.props.mealClick}>
                <img src={imageUrl} class="card-img-top" alt="..." />
                <div class="card-body" >
                    <h6 class="card-title">{meal.title}</h6>
                    {viewMeal}
                    {search}
                    <MealsModal show={this.state.showModal} closeFunc={() => this.setState({showModal: false})} changeMealFunc={changeMealFunc}></MealsModal>
                    
                </div>
            </div>
        )
    }
}

export default Meal;
