//           </div>
          
//           <Route path="/" exact component={MealPlan} />
//           <Route path="/meals" component={CreateMeal} />
//           <Route path="/logout" component={Logout} />
//         </div>
//       </Router>
//     );
//   }
// }

class Navbar extends Component{
  render(){
    return (
        <div>
          <nav class="navbar navbar-expand-lg" style={{ backgroundColor: "rgb(48, 51, 54)" }}>
                
                <Link to="/" class="navbar-brand">
                  <img class="d-inline-block align-middle" alt="Logo" src={logo} width="45" height="45"></img>
                  <span class="navbar-brand-text">BetterMeals</span>
                </Link>
                <div class="collapse navbar-collapse">
                  <ul class="nav navbar-nav navbar-right">
                    <li class='nav-item'><Link class="nav-link" to="/">Current Plan</Link></li>
                    <li class='nav-item'><Link class="nav-link" to="/meals">Meals</Link></li>
                    <li class='nav-item'><Link class="nav-link" to="/mealplans">Meal Plans</Link></li>
                  </ul>
                </div>
                <div class="justify-content-end navbar-collapse collapse">
                  <span class="navbar-text">
                      Logged in as Guest
                  </span>
                </div>
          </nav>
        </div>
        
      
    )
  }
}

class TodaysMeal extends Component{
  render(){
    return (
      <>
        <div class="btn-group" role="group">
            <button type="button" class="btn btn-secondary btn-lg">Today's Meals</button>
            <button type="button" class="btn btn-secondary btn-lg">Weekly Plan</button>
          </div>
          <div class="card text-center" id="breakfast">
            <div class="card-header">
              Breakfast
            </div>
            <div class="card-body">
              <h5 class="card-title">Random Meal</h5>
              <p class="card-text">A random meal that I created randomly, very cool</p>
              <a href="#" class="btn btn-primary">View Recipe</a>
            </div>
            <div class="card-footer text-muted">
              Basic Meal Plan #1
            </div>
          </div><div class="card text-center" id="lunch">
            <div class="card-header">
              Lunch
            </div>
            <div class="card-body">
              <h5 class="card-title">Random Meal #2</h5>
              <p class="card-text">A random meal that I created randomly, very cool</p>
              <a href="#" class="btn btn-primary">View Recipe</a>
            </div>
            <div class="card-footer text-muted">
              Basic Meal Plan #2
            </div>
          </div><div class="card text-center" id="dinner">
            <div class="card-header">
              Dinner
            </div>
            <div class="card-body">
              <h5 class="card-title">Random Meal #3</h5>
              <p class="card-text">A random meal that I created randomly, very cool</p>
              <a href="#" class="btn btn-primary">View Recipe</a>
            </div>
            <div class="card-footer text-muted">
              Basic Meal Plan #2
            </div>
          </div>
      </>
    )
  }
}

class Meals extends Component {
  render() {
      return (
          <div>
              <h1>Meal Plan!</h1>
              <p>Plan your meals out!</p>
          </div>
      )
  }
}
function useAuth(){
  const [auth, setAuth] = React.useState(false);
  const [user, setUser] = React.useState(null);
  const config = {
      headers:{
      "x-access-token": localStorage.getItem("token")
      }
  }
  
  axios.get('http://localhost:5000/user/isUserAuth', config)
      .then(res => {
          setAuth(res.data.isAuth);
          setUser(res.data.username)
      })
  return { auth, user }
}

function RequireAuth({ children }){
  const {auth, user} = useAuth();
  const history = useHistory();
  const location = useLocation();
  // alert("??");
  // console.log("HERE IT GOES, ITS GOING, GOING GREAT");
  console.log(auth); 
  return auth ? children : <Redirect to="/login" replace state={{ path: location.pathname }}></Redirect>;

  // if(authorized === false){
  //     alert("sigh");
  //     history.push("/meals"); 
  // }
  // else{
  //     return children;
  // }
 

}

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      currentPage: "/"
    }
  }

  pageChanged = () => {
    this.setState({
      currentPage: ":|"
    })
  }
  render() {
    return(
      <div class="bg">
        
        <Router>
          <Navbar></Navbar>
          <div class="container" id="todaysplancontainer">

            <Route exact path="/" render={() => <RequireAuth><TodaysMeal /></RequireAuth>} />
            <Route exact path="/meals" render={() => <RequireAuth><Meals /></RequireAuth>} />
            <Route exact path="/mealplans" render={() => <Mealplans />} />
            <Route exact path="/login" component={Login}></Route>
          </div>
        </Router>

      </div>
      
    )
  }
}

export default App;
