  // }
 

}

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      currentPage: "/"
    }
  }

  pageChanged = () => {
    this.setState({
      currentPage: ":|"
    })
  }
  render() {
    return(
      <div class="bg">
        
        <Router>
          
          

            <Route exact path="/" render={() => <RequireAuth><Navbar><TodaysMeal></TodaysMeal></Navbar></RequireAuth>} />
            <Route exact path="/meals" render={() => <RequireAuth><Navbar><Meals></Meals></Navbar></RequireAuth>} />
            <Route exact path="/mealplans" render={() => <><Navbar></Navbar><div class="container" id="todaysplancontainer"><Mealplans></Mealplans></div></>} />
            <Route exact path="/mealplan/:id" component={MealPlan} />
            <Route exact path="/login" component={Login}></Route>
     
        </Router>

      </div>
      
    )
  }
}

export default App;
