import React, { Component } from "react";
import axios from "axios";
import config from "../../config.json";
import { Accordion, Button } from 'react-bootstrap'
import { Link } from "react-router-dom";

class MealSnippet extends Component{
    constructor(props){
        super(props);
        this.state = {
            meal: {}
        }
    }

    componentDidMount(){
        const id = this.props.id
        if(id !== ""){
            const storageCheck = JSON.parse(localStorage.getItem(id));
            const options = {
                method: 'GET',
                url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + id + '/information',
                headers: {
                'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
                'X-RapidAPI-Key': config["api-key"]
                }
            };
            if(storageCheck && (Date.now() < storageCheck.ttl)){
                this.setState({
                    meal: storageCheck
                })
            }
            else{
                axios.request(options).then(res => {
                    res.data.ttl = Date.now() + (86400 * 1000);
                    localStorage.setItem(id, JSON.stringify(res.data));
                    this.setState({
                        meal: res.data
                    })
                            
                }).catch(err => {
                    console.error(err)
                })
            }
        }

        

    }

    render(){
        let meal = this.state.meal
        let pFontSize = 15
        if(this.props.id === ""){
            pFontSize = 20;
            meal.summary = "Head over to Meal Plans and select an Active plan!"
        }
        return(
            <>
                <img style={{width: "278px", height: "185px"}} src={"https://spoonacular.com/recipeImages/" + meal.id + "-556x370.jpg"} 
                align="left" class="col-md-8 float mb-3 mt-3 me-5 ms-md-3"></img>
                <h2 style={{marginTop: "1vh"}}>{meal.title}</h2>
                <p style={{margin: "2vw", fontSize: pFontSize + "px"}} dangerouslySetInnerHTML={{__html: meal.summary}}></p>
                <Link class="btn" style={{backgroundColor: "white", width: "100%", color: "black"}}>View More</Link>
            </>
        )
    }
}

export default MealSnippet;
