import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import emptysign from "../MealPlan/emptyplate.webp"

class Meal extends Component{
    constructor(props){
        super(props)
        this.state = {
            imageUrl: emptysign,
            meal: {title: "No Meal"},
            viewMeal: <></>
        }
        
    }
    setMealData = (id, data) => {
        console.log("please work");
        this.setState({
            imageUrl: "https://spoonacular.com/recipeImages/" + id + "-240x150.jpg",
            meal: data,
            viewMeal: <a href="#" style={{color: "rgb(41, 198, 254)"}}> View Meal</a>
        })
    }

    componentDidMount(){
        const {id} = this.props;
        console.log(typeof(id));
        console.log(this.state.imageUrl);
        const data = {};
        if(id !== ""){
            const options = {
                method: 'GET',
                url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + id + '/information',
                headers: {
                  'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
                  'X-RapidAPI-Key': '51be17977amsh1e7b4aa1af671dep16977ajsn18b85345a72c'
                }
            };
            
            axios.request(options).then(res => {
                // console.log(response.data);
                data = res.data
                this.setMealData(id, res.data);
            }).catch(function (error) {
                console.error(error);
            });
        }
        
    }
    render(){
        return(
            <div class="card" style={{width: "170px", backgroundColor: "rgb(48, 51, 54)", color: "whitesmoke"}}>
                <img src={this.state.imageUrl} class="card-img-top" alt="..." />
                <div class="card-body" >
                    <h6 class="card-title">{this.state.meal.title}</h6>
                    {this.state.viewMeal}
                </div>
            </div>
        )
    }
}

export default Meal;
