import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import emptysign from "../MealPlan/emptyplate.webp"
import MealsModal from "./MealsModal";

class Meal extends Component{
    constructor(props){
        super(props)
        this.state = {
            imageUrl: emptysign,
            meal: {title: "No Meal"},
            viewMeal: <a href="#" style={{color: "rgb(41, 198, 254)"}}> View Meal</a>
        }
        
    }

    componentDidMount(){
        const {id} = this.props;
        if(id !== ""){
            const storageCheck = JSON.parse(localStorage.getItem(id));
            const options = {
                method: 'GET',
                url: 'https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/' + id + '/information',
                headers: {
                  'X-RapidAPI-Host': 'spoonacular-recipe-food-nutrition-v1.p.rapidapi.com',
                  'X-RapidAPI-Key': '51be17977amsh1e7b4aa1af671dep16977ajsn18b85345a72c'
                }
            };
            if(storageCheck && (Date.now() < storageCheck.ttl)){
                this.setState({
                    imageUrl: "https://spoonacular.com/recipeImages/" + id + "-240x150.jpg",
                    meal: storageCheck,
                    viewMeal: <a href="#" style={{color: "rgb(41, 198, 254)"}}> View Meal</a>
                })
            }
            else{
                axios.request(options).then(res => {
                    res.data.ttl = Date.now() + (86400 * 1000);
                    localStorage.setItem(id, JSON.stringify(res.data));
                    this.setState({
                        imageUrl: "https://spoonacular.com/recipeImages/" + id + "-240x150.jpg",
                        meal: res.data,
                        viewMeal: <a href="#" style={{color: "rgb(41, 198, 254)"}}> View Meal</a>
                    })
                }).catch(function (error) {
                    console.error(error);
                });
            }
        
        }
        
    }
    render(){
        return(
            <div class="card" style={{width: "170px", backgroundColor: "rgb(48, 51, 54)", color: "whitesmoke", marginBottom: "2rem"}}>
                <img src={this.state.imageUrl} class="card-img-top" alt="..." />
                <div class="card-body" >
                    <h6 class="card-title">{this.state.meal.title}</h6>
                    {this.state.viewMeal}
                    <button class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#mealsModal"></button>
                    <div class='modal-dialog modal-dialog-scrollable' id="mealsModal">
                    </div>
                </div>
            </div>
        )
    }
}

export default Meal;
